pub fn calculate_square_under_line(k: f64, b:f64, x1: f64, x2: f64) -> f64 {
    let y2 = k * x2 + b;
    let rectange = y2 * (x2 - x1);
    let triangle = 0.5f64 * k * (x1 - x2) * (x2 - x1);
    return rectange + triangle
}
